import React from "react"

import { withFormik, Form, Field, ErrorMessage } from 'formik'

import styles from './index.module.css'

let Regist = () => {
  return (
    <div className={styles.root}>
      <form className={styles.form}>
        <div className={styles.header}>
          <p>注册</p>
        </div>
        <ul className={styles.content}>
          <li>
            <input type="text" placeholder="用户名"/>
          </li>
          <li>
            <input type="password" placeholder="新密码" id="pwd" />
          </li>
          <li>
            <input type="email" placeholder="邮箱" id="email" />
          </li>
          <li>
            <input type="date" id="birthday" />
          </li>
          <li className={styles.submit}>
            <input type="submit" value="登录"/>
          </li>
        </ul>
        <p className={styles.footer}>返回登录界面</p>
      </form>
    </div>
  )
}

const EDG_EMAIL = /^([A-Za-z0-9_\-\.])+\@([A-Za-z0-9_\-\.])+\.([A-Za-z]{2,4})$/

Regist=withFormik({
  mapPropsToValues:()=>({
    username:'',
    password:'',
    email:'',
    birthday:'',
  }),
  validate: ({ username, password,email,birthday }) => {
    let error = {}
    if (!username) {
        error.username = '用户名不能为空'
    }
    if (!password) {
        error.password = '密码不能为空'
    }
    if(!EDG_EMAIL.test(email)){
      error.email='邮箱格式错误'
    }
    if(!birthday)
    // if (username && password) setIsAble(true)
    // else setIsAble(false)

    return error
},
// 表单的提交事件
handleSubmit: async ({ username, password }) => {
    console.log(username, password)
    
}
})(Regist)

export default Regist
