import React, { useEffect, useState } from 'react'
import { useNavigate, useLocation,useParams, Link, Outlet } from 'react-router-dom'

import { Layout, Menu } from 'antd'

import homeimg from '../../assets/images/home/home.png'
import studentimg from '../../assets/images/home/student.png'

import styles from './index.module.css'

const { Header, Content, Footer, Sider } = Layout

const Home = () => {
  const navigate = useNavigate()
  const location = useLocation()
  const params=useParams()

  const [activeMenu,setActiveMenu]=useState(['1'])

  useEffect(()=>{
    console.log(location)
    window.onload(
      navigate('/home')
    )
    // if(location.pathname===)
    // if(location.pathname==='/home/students')setActiveMenu(['4'])
    
  })
  return (
    <Layout className={styles.root}>
      <Header className={styles.header}>
        <img src={studentimg} alt="student" />
        <p>Web技术课程演示系统</p>
        <img src={homeimg} alt="home" />
      </Header>
      <Content>
        <Layout className={styles.content}>
          <Sider
            className={styles.sider}
            breakpoint="md"
            collapsedWidth="0"
            onBreakpoint={(broken) => {
              console.log(broken)
            }}
            onCollapse={(collapsed, type) => {
              console.log(collapsed, type)
            }}
          >
            <div />
            <Menu mode="inline" defaultSelectedKeys={activeMenu} className={styles.menu}>
              <Menu.Item key="1">
                <Link to={`/home`}>首页</Link>
              </Menu.Item>
              <Menu.Item key="2">深蓝</Menu.Item>
              <Menu.Item key="3">橘黄</Menu.Item>
              <Menu.Item key="4">
                <Link to="/home/students">用户管理</Link>
              </Menu.Item>
              <Menu.Item key="5">文章管理</Menu.Item>
              <Menu.Item
                key="6"
                onClick={() => {
                  navigate('/forgetpassword')
                }}
              >
                修改密码
              </Menu.Item>
            </Menu>
          </Sider>
          <Outlet></Outlet>
        </Layout>
      </Content>
      <Footer className={styles.footer}>
        武汉理工大学 ©2022 Created by ChenHaizhen
      </Footer>
    </Layout>
  )
}

export default Home
