import React from "react"

import { withFormik, Form, Field, ErrorMessage } from 'formik'

import styles from './index.module.css'

let ForgetPassword=()=>{
  return (
    <div className={styles.root}>
      <form className={styles.form}>
        <div className={styles.header}>
          <p>修改密码</p>
        </div>
        <ul className={styles.content}>
          <li>
            <Field name="username" placeholder="用户名"></Field>
            <ErrorMessage name="username" component="div"></ErrorMessage>
          </li>
          <li>
            <Field name="password" placeholder="密码" type="password"></Field>
            <ErrorMessage name="password" component="div"></ErrorMessage>
          </li>
          <li>
          <Field name="confirmPassword" placeholder="确认密码" type="password"></Field>
            <ErrorMessage name="confirmPassword" component="div"></ErrorMessage>
          </li>
          <li className={styles.submit}>
            <input type="submit" value="确认" />
          </li>
        </ul>
        <p className={styles.footer}>返回登录界面</p>
      </form>
    </div>
  )
}

ForgetPassword= withFormik({
  mapPropsToValues: () => ({
    username: '',
    password: '',
    confirmPassword:'',
  }),
  validate: ({ username, password, email, birthday }) => {
    let error = {}
    if (!username) {
      error.username = '用户名不能为空'
    }
    if (!password) {
      error.password = '密码不能为空'
    }
    if (confirmPassword!==password) {
      error.confirmPassword = '出生日期不能为空'
    }
    // if (username && password) setIsAble(true)
    // else setIsAble(false)

    return error
  },
  // 表单的提交事件
  handleSubmit: async ({ username, password }) => {
    console.log(username, password)
  },
})(ForgetPassword)

export default ForgetPassword
